import React from 'react';
import './styles.css';
import ImageUploader from './ImageUploader';
import DynamoDBData from './DynamoDBData';
import Register from './Register';
import Login from './Login';
import { Route, Link, Navigate } from 'react-router-dom';

import { ModerateCountProvider } from './ModerateCountContext'; // Import the context
import { useOktaAuth } from '@okta/okta-react'; // Import the Okta hook

function App() {
  const { authState, authService } = useOktaAuth();

  const handleLogin = () => {
    authService.login('/customer_service'); // Redirect to /customer_service after login
  };

  const handleLogout = () => {
    authService.logout('/'); // Redirect to the root URL after logout
  };

  return (
    <ModerateCountProvider> {/* Wrap Router with the context provider */}
      <Router>
        <div className="App">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to="/" className="navbar-brand">
              Home
            </Link>
            <ul className="navbar-nav ml-auto">
              {authState.isAuthenticated ? (
                <>
                  <li className="nav-item">
                    <Link to="/customer_service" className="nav-link">
                      Moderation Results
                    </Link>
                  </li>
                  <li className="nav-item">
                    <button onClick={handleLogout} className="nav-link btn-link">
                      Logout
                    </button>
                  </li>
                </>
              ) : (
                <li className="nav-item">
                  <button onClick={handleLogin} className="nav-link btn-link">
                    Login
                  </button>
                </li>
              )}
            </ul>
          </nav>
          <Routes>
            <Route path="/" element={<ImageUploader />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            {authState.isAuthenticated ? (
              <Route path="/customer_service" element={<DynamoDBData />} />
            ) : (
              <Route path="/customer_service" element={<Navigate to="/login" />} />
            )}
          </Routes>
        </div>
      </Router>
    </ModerateCountProvider>
  );
}

export default App;
