import React, { useState } from 'react';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const Login = () => {
  const { oktaAuth } = useOktaAuth();
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    setIsLoading(true);

    try {
      // Redirect the user to the Okta sign-in page
      await oktaAuth.signInWithRedirect();

      // After successful Okta login, obtain the Okta token
      const authState = await oktaAuth.authStateManager.getAuthState();
      const oktaToken = authState.idToken;

      // Send the Okta token to your server's /login endpoint
      const response = await axios.post('http://localhost:5000/login', { okta_token: oktaToken });

      // Store the access token in local storage
      localStorage.setItem('token', response.data.access_token);

      // You can now navigate to the protected route if needed
      // Example: navigate('/customer_service');
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };

  return (
    <div>
      <button onClick={handleLogin} disabled={isLoading}>
        {isLoading ? 'Logging In...' : 'Log In with Okta'}
      </button>
    </div>
  );
};

export default Login;
