import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useOktaAuth } from '@okta/okta-react';

import './Login.css';

const Login = () => {
  const navigate = useNavigate();
  const { authState, authService } = useOktaAuth();

  useEffect(() => {
    if (authState.isAuthenticated) {
      navigate('/customer_service'); // Redirect to the home page if authenticated
    }
  }, [authState, navigate]);

  const handleOktaSignIn = () => {
    authService.login('/customer_service'); // Redirect to the specified URL after login
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login with Okta</h2>
        <button onClick={handleOktaSignIn} className="okta-sign-in-button">
          Sign In with Okta
        </button>
        {/* You can add additional content or messages here */}
      </div>
    </div>
  );
};

export default Login;
