import React, { useState } from 'react';
import './styles.css';
import ImageUploader from './ImageUploader';
import DynamoDBData from './DynamoDBData';
import Register from './Register';
import Login from './Login';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import { ModerateCountProvider } from './ModerateCountContext'; // Import the context
import { Security, SecureRoute, LoginCallback } from '@okta/okta-react';
import { OktaAuth, toRelativeUrl } from '@okta/okta-auth-js';
import config from './oktaConfig'; // Import your Okta configuration

const oktaAuth = new OktaAuth(config.oidc);



function App() {
  const [user, setUser] = useState(null);

  const handleLogin = (username) => {
    setUser(username);
  };

  const handleLogout = () => {
    setUser(null);
  };

  const customAuthHandler = () => {
    // Redirect to the login route if authentication is required
    // You can customize this behavior as needed
    // For example, you can use <Navigate to="/login" /> instead
    history.push('/login');
  };

  const restoreOriginalUri = async (_oktaAuth, originalUri) => {
    // Restore the original URI after login (optional)
    history.replace(toRelativeUrl(originalUri || '', window.location.origin));
  };

  return (
    <ModerateCountProvider>
      <Router>
        <div className="App">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to="/" className="navbar-brand">
              Home
            </Link>
            <ul className="navbar-nav ml-auto">
              {user ? (
                <>
                  <li className="nav-item">
                    <Link to="/customer_service" className="nav-link">
                      Moderation Results
                    </Link>
                  </li>
                  <li className="nav-item">
                    <button onClick={handleLogout} className="nav-link btn-link">
                      Logout
                    </button>
                  </li>
                </>
              ) : (
                <li className="nav-item">
                  <Link to="/login" className="nav-link">
                    Login
                  </Link>
                </li>
              )}
            </ul>
          </nav>
          <Routes>
            <Route path="/" element={<ImageUploader />} />
            <Route path="/register" element={<Register />} />
            <Route
              path="/login"
              element={
                <Login
                  onLogin={handleLogin}
                  oktaAuth={oktaAuth} // Pass oktaAuth as a prop
                />
              }
            />
            {user ? (
              <SecureRoute path="/customer_service" element={<DynamoDBData />} />
            ) : (
              <Route path="/customer_service" element={<Navigate to="/login" />} />
            )}
            <Route path="/login/callback" element={<LoginCallback oktaAuth={oktaAuth} />} />
          </Routes>
        </div>
      </Router>
    </ModerateCountProvider>
  );
}
