import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const Login = () => {
  const { oktaAuth } = useOktaAuth();
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    setIsLoading(true);

    try {
      // Redirect the user to the Okta sign-in page
      await oktaAuth.signInWithRedirect();
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };

  const sendTokenToServer = async (token) => {
    try {
      // Send the Okta token to your server's /login endpoint
      await axios.post('http://localhost:5000/login', { okta_token: token });
    } catch (error) {
      console.error('Error sending token to server:', error);
    }
  };

  // Listen for the Okta authentication event
  oktaAuth.authStateManager.subscribe(async (authState) => {
    if (authState.isAuthenticated) {
      // Send the Okta token to your server when the user is authenticated
      sendTokenToServer(authState.idToken);
    }
  });

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <button
          onClick={handleLogin}
          className="login-button"
          disabled={isLoading}
        >
          {isLoading ? 'Logging In...' : 'Log In'}
        </button>
        <Link to="/register" className="register-link">
          Don't have an account? Register here
        </Link>
      </div>
    </div>
  );
};

export default Login;
