import React, { useState, useEffect } from 'react';
import './styles.css';
import ImageUploader from './ImageUploader';
import DynamoDBData from './DynamoDBData';
import Register from './Register';
import Login from './Login';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import { ModerateCountProvider } from './ModerateCountContext';
import { Security, useOktaAuth } from '@okta/okta-react'; // Import useOktaAuth
import oktaConfig from './oktaConfig';

function App() {
  const { authState, oktaAuth } = useOktaAuth(); // Destructure authState and oktaAuth
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if the user is authenticated and set the user state accordingly
    if (authState.isAuthenticated) {
      setUser(authState.idToken.claims.email); // Set the user's email as an example
    } else {
      setUser(null);
    }
  }, [authState]);

  const handleLogout = () => {
    oktaAuth.signOut(); // Sign out using Okta
  };

  return (
    <ModerateCountProvider>
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand">
            Home
          </Link>
          <ul className="navbar-nav ml-auto">
            {user ? (
              <>
                <li className="nav-item">
                  <Link to="/customer_service" className="nav-link">
                    Moderation Results
                  </Link>
                </li>
                <li className="nav-item">
                  <button onClick={handleLogout} className="nav-link btn-link">
                    Logout
                  </button>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <Link to="/login" className="nav-link">
                  Login
                </Link>
              </li>
            )}
          </ul>
        </nav>
        <Security oktaAuth={oktaAuth}>
          <Routes>
            <Route path="/" element={<ImageUploader />} />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
            {user ? (
              <Route path="/customer_service" element={<DynamoDBData />} />
            ) : (
              <Navigate to="/login" /> // Redirect to login page if not authenticated
            )}
          </Routes>
        </Security>
      </div>
    </ModerateCountProvider>
  );
}

export default App;
