import React, { Component } from 'react';
import { withOktaAuth } from '@okta/okta-react';
import './Login.css';

class Login extends Component {
  constructor(props) {
    super(props);
    this.login = this.login.bind(this);
    this.logout = this.logout.bind(this);
  }

  async login() {
    // Use signInWithRedirect from props
    await this.props.authService.signInWithRedirect();
  }

  async logout() {
    await this.props.authService.signOut();
  }

  componentDidUpdate(prevProps) {
    // Check if the authState has changed from not authenticated to authenticated
    if (!prevProps.authState.isAuthenticated && this.props.authState.isAuthenticated) {
      // Redirect to /customer_service after successful login
      this.props.history.push('/customer_service');
    }
  }

  render() {
    let body = null;
    if (this.props.authState?.isAuthenticated) {
      body = (
        <div className="Buttons">
          <button onClick={this.logout}>Logout</button>
          {/* Add your authenticated content here */}
        </div>
      );
    } else {
      body = (
        <div className="Buttons">
          <button onClick={this.login}>Login</button>
        </div>
      );
    }

    return (
      <div className="Login-container">
        <div className="Login-box">
          <h2>Login</h2>
          {body}
        </div>
      </div>
    );
  }
}

export default withOktaAuth(Login);
