 import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useOktaAuth } from '@okta/okta-react';
import { useModerateCount } from './ModerateCountContext';

const Login = () => {
  const navigate = useNavigate();
  const { oktaAuth } = useOktaAuth();
  const [totalImageCount, setTotalImageCount] = useState(0);
  const { moderateCount, setModerateCount } = useModerateCount();
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    axios
      .get('http://localhost:5000/image_count')
      .then((response) => {
        setTotalImageCount(response.data.imageCount);
      })
      .catch((error) => {
        console.error('Error fetching image count:', error);
      });
  }, []);

  const handleLogin = async () => {
    if (moderateCount > 0) {
      setIsLoading(true);

      try {
        // Redirect the user to the Okta sign-in page
        await oktaAuth.signInWithRedirect();
      } catch (error) {
        console.error('Login error:', error);
        setIsLoading(false);
      }
    } else {
      console.error('Moderate count is invalid');
      setIsLoading(false);
    }
  };

  // Listen for Okta authentication events
  oktaAuth.authStateManager.subscribe(async (authState) => {
    if (authState.isAuthenticated) {
      // Get the Okta access token
      const accessToken = authState.accessToken.value;

      // Make a request to your server's /login endpoint with the access token in the headers
      try {
        const response = await axios.post(
          'http://localhost:5000/login',
          { moderate_count: moderateCount },
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );

        if (response.data.access_token) {
          // Store the access token in local storage
          localStorage.setItem('token', response.data.access_token);

          // Optionally, you can also navigate to the protected route here if needed.
          // navigate('/customer_service');
        } else {
          console.error('Access token is missing in the server response');
        }
      } catch (error) {
        console.error('Error obtaining access token:', error);
      }
    }
  });


  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <p>Total Number of Images Pending: {totalImageCount}</p>
        <input
          type="number"
          placeholder="Number of Images to Moderate"
          value={moderateCount}
          onChange={(e) => setModerateCount(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="login-button"
          disabled={isLoading}
        >
          {isLoading ? 'Logging In...' : 'Log In'}
        </button>
        <Link to="/register" className="register-link">
          Don't have an account? Register here
        </Link>
      </div>
    </div>
  );
};

export default Login;
