import React, { useState } from 'react';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const Login = () => {
  const { oktaAuth } = useOktaAuth();
  const [isLoading, setIsLoading] = useState(false);

const handleLogin = async () => {
  setIsLoading(true);

  try {
    // Redirect the user to the Okta sign-in page
    await oktaAuth.signInWithRedirect();
  } catch (error) {
    console.error('Login error:', error);
    setIsLoading(false);
  }
};

// In your OktaAuth setup (outside the component), you can add an event handler
// to listen for successful authentication and then send the token to your server.
oktaAuth.authStateManager.subscribe(async (authState) => {
  if (authState.isAuthenticated) {
    const oktaToken = authState.idToken;

    // Send the Okta token to your server's /login endpoint
    try {
      const response = await axios.post('http://localhost:5000/login', { okta_token: oktaToken });

      if (response.data.access_token) {
        // Store the token securely (localStorage or sessionStorage)
        localStorage.setItem('token', response.data.access_token);

        // You can now navigate to the protected route if needed
        // Example: navigate('/customer_service');
      } else {
        console.error('Access token not received');
      }
    } catch (error) {
      console.error('Error sending token to server:', error);
    }
  }
