import React, { Component } from 'react';
import './LoginModal.css';

class LoginModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: '',
      password: '',
      errorMessage: '',
    };
  }

  handleInputChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };

  handleLogin = async () => {
    const { username, password } = this.state;

    try {
      const response = await fetch('https://your-okta-domain.okta.com/api/v1/authn', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username,
          password,
        }),
      });

      if (response.ok) {
        // Login was successful
        this.props.onLogin(); // Call a callback to handle successful login
      } else {
        const data = await response.json();
        this.setState({ errorMessage: data.errorSummary });
      }
    } catch (error) {
      console.error('Login error:', error);
      this.setState({ errorMessage: 'An error occurred during login.' });
    }
  };

  render() {
    const { username, password, errorMessage } = this.state;

    return (
      <div className="login-modal">
        <h2>Login</h2>
        <input
          type="text"
          name="username"
          placeholder="Username/Email"
          value={username}
          onChange={this.handleInputChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={this.handleInputChange}
        />
        <button onClick={this.handleLogin}>Login</button>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>
    );
  }
}

export default LoginModal;
