// LoginCallback.js
import React, { useEffect } from 'react';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const LoginCallback = () => {
  const { oktaAuth } = useOktaAuth();

  useEffect(() => {
    const handleLoginCallback = async () => {
      try {
        // Parse the access token from the URL
        const authState = await oktaAuth.handleLoginRedirect();

        // Extract the Okta token
        const oktaToken = authState.idToken;
        console.log("Okta Token:", oktaToken);

        // Send the Okta token to your server's /login endpoint
        axios
          .post('http://localhost:5000/login', { okta_token: oktaToken })
          .then((response) => {
            if (response.data.access_token) {
              // Store the token securely (localStorage or sessionStorage)
              localStorage.setItem('token', response.data.access_token);

              // Redirect to a protected route or perform any other actions
              // Example: navigate('/customer_service');
            } else {
              console.error('Access token not received');
            }
          })
          .catch((error) => {
            console.error('Error sending token to server:', error);
          });
      } catch (error) {
        console.error('Login callback error:', error);
      }
    };

    handleLoginCallback();
  }, [oktaAuth]);

  return (
    <div>
      <p>Processing login...</p>
    </div>
  );
};

export default LoginCallback;
