import React, { useEffect } from 'react';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const LoginCallback = () => {
  const { oktaAuth } = useOktaAuth();

  useEffect(() => {
    const handleTokenExchange = async () => {
      try {
        // Parse the Okta ID token from the URL parameters
        const { tokens } = await oktaAuth.handleRedirect();



        if (tokens && tokens.idToken) {
          const oktaToken = tokens.idToken;

          // Send the Okta ID token to your server's /login endpoint
          axios
            .post('http://localhost:5000/login', { oktaToken })
            .then((response) => {
              if (response.data.sessionToken) {
                // Store the session token securely (localStorage or sessionStorage)
                localStorage.setItem('sessionToken', response.data.sessionToken);

                // Redirect to a protected route or perform any other actions
                // Example: navigate('/customer_service');
              } else {
                console.error('Session token not received');
                // Handle this error case, e.g., display a message to the user
              }
            })
            .catch((error) => {
              console.error('Error sending token to server:', error);
              // Handle this error case, e.g., display a message to the user
            });
        }
      } catch (error) {
        console.error('Token exchange error:', error);
        // Handle this error case, e.g., display a message to the user
      }
    };

    handleTokenExchange();
  }, [oktaAuth]);

  return <div>Logging in...</div>;
};

export default LoginCallback;
