import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useOktaAuth } from '@okta/okta-react';
import { useModerateCount } from './ModerateCountContext';

const Login = () => {
  const navigate = useNavigate();
  const { oktaAuth } = useOktaAuth();
  const [totalImageCount, setTotalImageCount] = useState(0);
  const { moderateCount, setModerateCount } = useModerateCount();
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    axios
      .get('http://localhost:5000/image_count')
      .then((response) => {
        setTotalImageCount(response.data.imageCount);
      })
      .catch((error) => {
        console.error('Error fetching image count:', error);
      });
  }, []);

  const handleLogin = async () => {
    if (moderateCount > 0) {
      setIsLoading(true);
      axios.post('http://localhost:5000/login', { moderate_count: moderateCount })
        .then(response => {
          if (response.data.access_token) {
            // Store the token securely (localStorage or sessionStorage)
            localStorage.setItem('token', response.data.access_token);

            // Use a function that can be awaited
            const loginWithOkta = async () => {
              try {
                // Redirect the user to the Okta sign-in page 
                await oktaAuth.signInWithRedirect();
                navigate('/customer_service');
              } catch (error) {
                console.error('Login error:', error);
                setIsLoading(false);
              }
            };

            loginWithOkta();
          } else {
            console.error('Moderate count is invalid');
            setIsLoading(false);
          }
        })
        .catch(error => {
          console.error('Login request error:', error);
          setIsLoading(false);
        });
    } else {
      console.error('Moderate count is invalid');
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <p>Total Number of Images Pending: {totalImageCount}</p>
        <input
          type="number"
          placeholder="Number of Images to Moderate"
          value={moderateCount}
          onChange={(e) => setModerateCount(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="login-button"
          disabled={isLoading}
        >
          {isLoading ? 'Logging In...' : 'Log In'}
        </button>
        <Link to="/register" className="register-link">
          Don't have an account? Register here
        </Link>
      </div>
    </div>
  );
};

export default Login;
