import React, { useState, useEffect } from 'react';
import './styles.css';
import ImageUploader from './ImageUploader';
import DynamoDBData from './DynamoDBData';
import Register from './Register';
import Login from './Login';
import Logout from './logout'; // Corrected the import
import LoginCallback from './LoginCallback';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import { ModerateCountProvider } from './ModerateCountContext';
import { Security, SecureRoute, useOktaAuth } from '@okta/okta-react';
import oktaConfig from './oktaConfig';

function App() {
  const { oktaAuth, authState } = useOktaAuth();
  const [user, setUser] = useState(null);

  const handleLogin = (username) => {
    setUser(username);
  };

  useEffect(() => {
    console.log(authState);
    // Check if authState is defined before accessing its properties
    if (authState && authState.isAuthenticated) {
      // Check if a redirection is already in progress
      if (!authState.isPending) {
        // User is authenticated, initiate redirection to the Okta callback route
        oktaAuth.token.getWithRedirect();
      }
    }
  }, [authState, oktaAuth]);


  return (
    <ModerateCountProvider>
      <div className="App">
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
          <Link to="/" className="navbar-brand">
            Home
          </Link>
          <ul className="navbar-nav ml-auto">
            {user ? (
              <>
                <li className="nav-item">
                  <Link to="/customer_service" className="nav-link">
                    Moderation Results
                  </Link>
                </li>
                <li className="nav-item">
                  <Logout /> {/* Use the Logout component */}
                </li>
              </>
            ) : (
              <li className="nav-item">
                <Login onLogin={handleLogin} />
              </li>
            )}
          </ul>
        </nav>
        {/* <Security oktaAuth={oktaConfig}> */}
          <Routes>
            <Route path="/" element={<ImageUploader />} />
            <Route path="/register" element={<Register />} />
            <Route path="/login/callback" element={<LoginCallback />} />
            {user ? (
              <SecureRoute path="/customer_service" element={<DynamoDBData />} />
            ) : (
              <Route path="/customer_service" element={<Navigate to="/login" />} />
            )}
          </Routes>
        {/* </Security> */}
      </div>
    </ModerateCountProvider>
  );
}

export default App;
