import React, { useState, useEffect } from 'react';
import './styles.css';
import ImageUploader from './ImageUploader';
import DynamoDBData from './DynamoDBData';
import Register from './Register';
import Login from './Login';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import { Security, SecureRoute, LoginCallback } from '@okta/okta-react'; // Import Okta components
import oktaAuth from './oktaAuth'; // Import your Okta instance

import { ModerateCountProvider } from './ModerateCountContext';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    oktaAuth.tokenManager.on('tokenChanged', () => {
      setUser(oktaAuth.tokenManager.get('idToken'));
    });
  }, []);

  const handleLogin = () => {
    // Not needed, as the user is automatically authenticated via Okta.
  };

  const handleLogout = () => {
    oktaAuth.signOut();
  };

  return (
    <ModerateCountProvider>
      <Router>
        <div className="App">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <Link to="/" className="navbar-brand">
              Home
            </Link>
            <ul className="navbar-nav ml-auto">
              {user ? (
                <>
                  <li className="nav-item">
                    <Link to="/customer_service" className="nav-link">
                      Moderation Results
                    </Link>
                  </li>
                  <li className="nav-item">
                    <button onClick={handleLogout} className="nav-link btn-link">
                      Logout
                    </button>
                  </li>
                </>
              ) : (
                <li className="nav-item">
                  <Link to="/login" className="nav-link">
                    Login
                  </Link>
                </li>
              )}
            </ul>
          </nav>
          <Routes>
            <Route path="/" element={<ImageUploader />} />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <SecureRoute path="/customer_service" element={<DynamoDBData />} />
            <Route path="/implicit/callback" element={<LoginCallback />} />
          </Routes>
        </div>
      </Router>
    </ModerateCountProvider>
  );
}

export default App;
