import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Login.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import { useOktaAuth } from '@okta/okta-react'; // Import useOktaAuth for Okta authentication
import { useModerateCount } from './ModerateCountContext';
import jwt_decode from 'jwt-decode';

const Login = ({ onLogin }) => {
  const navigate = useNavigate(); // Use useNavigate to navigate
  const { oktaAuth } = useOktaAuth(); // Use useOktaAuth to access Okta authentication
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [totalImageCount, setTotalImageCount] = useState(0);
  const { moderateCount, setModerateCount } = useModerateCount();
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:5000/image_count')
      .then(response => {
        setTotalImageCount(response.data.imageCount);
      })
      .catch(error => {
        console.error('Error fetching image count:', error);
      });
  }, []);

  const handleLogin = async () => {
    if (username && password && moderateCount > 0) {
      setIsLoading(true);

      try {
        const response = await oktaAuth.signInWithCredentials({ username, password });
        if (response.status === 'SUCCESS') {
          onLogin(username);
          setUsername(username);
          navigate('/customer_service');
        } else {
          setErrorMessage('Invalid username or password');
        }
      } catch (error) {
        console.error('Login error:', error);
        setErrorMessage('Username or password incorrect');
      } finally {
        setIsLoading(false);
      }
    } else {
      setErrorMessage('Username, password, and a valid number of images to moderate are required');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <p>Total Number of Images Pending: {totalImageCount}</p>
        <input
          type="number"
          placeholder="Number of Images to Moderate"
          value={moderateCount}
          onChange={(e) => setModerateCount(e.target.value)}
        />
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="login-button"
          disabled={isLoading}
        >
          {isLoading ? 'Logging In...' : 'Log In'}
        </button>
        <Link to="/register" className="register-link">
          Don't have an account? Register here
        </Link>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>
    </div>
  );
};

export default Login;
