import React, { useState } from 'react';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const Login = () => {
  const { oktaAuth } = useOktaAuth();
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = async () => {
    setIsLoading(true);

    try {
      // Redirect the user to the Okta sign-in page
      await oktaAuth.signInWithRedirect();

      // After successful Okta login, obtain the Okta token
      const authState = await oktaAuth.authStateManager.getAuthState();
      const oktaToken = 'authState.idToken';

      // Create an Axios instance with a custom configuration
      const axiosInstance = axios.create({
        baseURL: 'http://localhost:5000', // Set your server's base URL
        headers: {
          Authorization: `Bearer ${oktaToken}`, // Send Okta token in the Authorization header
          'Content-Type': 'application/json', // Specify the content type if needed
        },
      });

      // Send a POST request to your server's /login endpoint using the custom Axios instance
      const response = await axiosInstance.post('/login', { okta_token: oktaToken });

      if (response.data.access_token) {
        // Store the token securely (localStorage or sessionStorage)
        localStorage.setItem('token', response.data.access_token);

        // You can now navigate to the protected route if needed
        // Example: navigate('/customer_service');
      } else {
        console.error('Access token not received');
      }
    } catch (error) {
      console.error('Login error:', error);
      setIsLoading(false);
    }
  };


  return (
    <div>
      <button onClick={handleLogin} disabled={isLoading}>
        {isLoading ? 'Logging In...' : 'Log In with Okta'}
      </button>
    </div>
  );
};

export default Login;
