import React, { useEffect } from 'react';
import { useOktaAuth } from '@okta/okta-react';
import axios from 'axios';

const LoginCallback = () => {
  const { oktaAuth } = useOktaAuth();

  useEffect(() => {
    const handleTokenExchange = async () => {
      try {
        // Handle the Okta redirect after successful login
        await oktaAuth.handleRedirect();

        // Retrieve the Okta token storage data
        const oktaTokenStorage = JSON.parse(localStorage.getItem('okta-token-storage'));

        if (!oktaTokenStorage || !oktaTokenStorage.tokens || !oktaTokenStorage.tokens.idToken || !oktaTokenStorage.tokens.idToken.value) {
          console.error('Access token not found in Okta token storage');
          // Handle this error case, e.g., display a message to the user
          return;
        }

        const accessToken = oktaTokenStorage.tokens.idToken.value;

        localStorage.setItem('blabooom', acce);

        console.log('Retrieved access token:', accessToken);

        axios
          .post('http://localhost:5000/login', { access_token: accessToken })
          .then((response) => {
            if (response.data.access_token) {
              // Store the token securely (localStorage or sessionStorage)
              localStorage.setItem('token', response.data.access_token);

              // Redirect to the /customer_service route or perform other actions
              // Example: navigate('/customer_service');
            } else {
              console.error('Access token not received');
              // Handle this error case, e.g., display a message to the user
            }
          })
          .catch((error) => {
            console.error('Error sending token to server:', error);
            // Handle this error case, e.g., display a message to the user
          });
      } catch (error) {
        console.error('Token exchange error:', error);
        // Handle this error case, e.g., display a message to the user
      }
    };

    handleTokenExchange();
  }, [oktaAuth]);

  return <div>Logging in...</div>;
};

export default LoginCallback;
